import React from 'react';
import { signup } from '../api/auth';

export default function SignupPage(){
  const [u,setU]=React.useState('');
  const [p,setP]=React.useState('');
  const [msg,setMsg]=React.useState(null);

  async function onSubmit(e){
    e.preventDefault();
    const res = await signup({username:u,password:p});
    if (res.ok) setMsg('Account created, please sign in'); else setMsg(res.error || JSON.stringify(res));
  }

  return (
    <div>
      <h2>Sign up</h2>
      <form onSubmit={onSubmit}>
        <div><label>Username<input value={u} onChange={e=>setU(e.target.value)}/></label></div>
        <div><label>Password<input type="password" value={p} onChange={e=>setP(e.target.value)}/></label></div>
        <button type="submit">Create</button>
      </form>
      {msg && <div>{msg}</div>}
    </div>
  );
}
