import React from 'react';
import { signin } from '../api/auth';

export default function SigninPage({onAuth}){
  const [u,setU]=React.useState('');
  const [p,setP]=React.useState('');
  const [msg,setMsg]=React.useState(null);

  async function onSubmit(e){
    e.preventDefault();
    const res = await signin({username:u,password:p});
    if (res.token) { localStorage.setItem('token',res.token); setMsg('Signed in'); if (onAuth) onAuth(res); } else setMsg(res.error || JSON.stringify(res));
  }

  return (
    <div>
      <h2>Sign in</h2>
      <form onSubmit={onSubmit}>
        <div><label>Username<input value={u} onChange={e=>setU(e.target.value)}/></label></div>
        <div><label>Password<input type="password" value={p} onChange={e=>setP(e.target.value)}/></label></div>
        <button type="submit">Sign in</button>
      </form>
      {msg && <div>{msg}</div>}
    </div>
  );
}
