import React from 'react';
import LogsPage from './pages/LogsPage';
import SearchPage from './pages/SearchPage';
import AlertsPage from './pages/AlertsPage';
import IndexPage from './pages/IndexPage';
import SignupPage from './pages/SignupPage';
import SigninPage from './pages/SigninPage';

export default function App(){
  const [route, setRoute] = React.useState('index');
  const [user, setUser] = React.useState(null);
  function handleAuth(info){ setUser(info); setRoute('logs'); }
  return (
    <div className="app">
      <header>
        <h1>Splunk lite</h1>
        <nav>
          <button onClick={()=>setRoute('index')}>Home</button>
          <button onClick={()=>setRoute('logs')}>Logs</button>
          <button onClick={()=>setRoute('search')}>Search</button>
          <button onClick={()=>setRoute('alerts')}>Alerts</button>
          <button onClick={()=>setRoute('signup')}>Sign up</button>
          <button onClick={()=>setRoute('signin')}>Sign in</button>
        </nav>
      </header>
      <main>
        {route === 'index' && <IndexPage/>}
        {route === 'signup' && <SignupPage/>}
        {route === 'signin' && <SigninPage onAuth={handleAuth}/>}        
        {route === 'logs' && <LogsPage/>}
        {route === 'search' && <SearchPage/>}
        {route === 'alerts' && <AlertsPage/>}
      </main>
    </div>
  );
}
